import axios from 'axios';
import queryString from 'query-string';

export const writePost = ({title,body, tags}) => axios.post('/api/posts',
{title,body,tags});

export const getPost = (id) => axios.get(`/api/posts/${id}`);

export const getPostList = ({tag,page})=> axios.get(`/api/posts/?${queryString.stringify({tag,page})}`);
export const editPost = ({title,body,tags,id}) => axios.patch(`/api/posts/${id}`,{title,body,tags});
